INSERT INTO `sppd` VALUES (21, 1, '005/007.5-BPKAD/2020', 'BPKAD Kota Serang ', 'Undangan Rekonsiliasi  Realisasi Belanja', 'Selasa', '2020-01-14', '2020-01-14', '09.00 s/d Selesai', 272, 'Bidang Akuntansi BPKAD Kota Serang', 'Rekonsiliasi Realisasi Anggaran Belanja Bulan Desember 2019                                                                ', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-14', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Rekonsiliasi Realisasi Anggaran Belanja Bulan Desember 2019 pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar&nbsp; Daerah ke Bidang Akuntansi BPKAD Kota Serang pada Ha</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">ri Selasa Tanggal 14 Januari 2020.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan Megirim Rekonsiliasi Realisasi Anggaran Belanja Bulan Desember 2019 adalah kegiatan ini dilaksanakan sehubungan dengan transparasi dan akuntabilitas kinerja OPD didalam penyerapan Belanja APBD Kota Serang Tahun Anggaran 2019. Pada hal ini, Dinas Komunikasi dan Informatika Kota Serang pada bulan Desember 2019 sudah melaksanakan Rekonsiliasi dengan data yang sudah sesuai/balance</span></p>', 1, 9, 'QR-4WwoVE-198208132009022002-.png', '198208132009022002', 121001);
