INSERT INTO `sppd` VALUES (28, 1, '', '', '', 'Senin', '2020-01-27', '2020-01-27', '08.00 s/d selesai', 272, 'Bidang Perbendaharaan Belanja Langsung BPKAD', 'Mengirim SPM TU                                ', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-27', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Mengirim SPM TU</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar&nbsp; Daerah ke&nbsp;</span><span style=\"color: rgb(33, 37, 41);\">Bidang Perbendaharaan Belanja Langsung BPKAD</span><span style=\"color: rgb(33, 37, 41);\">&nbsp;Kota Serang</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">&nbsp;pada Ha</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">ri Senin Tanggal 27 Januari 2020.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;</span><span style=\"color: rgb(33, 37, 41);\">Mengirim SPM TU</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;adalah tersampaikannya SPM TU Kegiatan Penyebarluasan Informasi Penyelenggaraan Pemerintah Daerah dengan Nomor SPM : 007/Diskominfo/TU/I/2020 di BPKAD Kota Serang Bidang Perbendaharaan Belanja Langsung.&nbsp;</span></p>', 1, 9, 'QR-JkrtWE-198208132009022002-.png', '198208132009022002', 121001);
