INSERT INTO `sppd` VALUES (31, 1, '', '', '', 'Kamis', '2020-01-30', '2020-01-30', '08.00 s/d selesai', 272, 'KPPN Kota Serang', 'Melaporkan DTH Dinas Komunikasi dan Informatika Kota Serang', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-30', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Melaporkan DTH Dinas Komunikasi dan Informatika Kota Serang</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar&nbsp; Daerah ke&nbsp;</span><span style=\"color: rgb(33, 37, 41);\">KPPN Kota Serang</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">&nbsp;pada Ha</span><span style=\"font-size: 1rem; color: rgb(33, 37, 41);\">ri Kamis Tanggal 30 Januari 2020.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;</span><span style=\"color: rgb(33, 37, 41);\">Melaporkan DTH Dinas Komunikasi dan Informatika Kota Serang</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;adalah bahwa Dinas Komunikasi dan Informatika Kota Serang telah melaporkan Pajak-pajak yang sudah dibayar dari bulan Juli samapi dengan bulan Desember 2019.</span></p>', 1, 9, 'QR-aEPoRi-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (32, 1, '005/45.2-Diskominfo/I/2020', 'Diskominfo Kota Serang', 'Undangan', 'Kamis', '2020-02-06', '2020-02-06', '08.00 s/d selesai', 272, 'Hotel Wisata Baru', 'Menghadiri Kegiatan Rapat Koordinasi Pejabat Pengelola Informasi dan Dokumentasi (PPID) Tahun 2020', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-06', '<ul><li>Rapat Koordinasi Pejabat Pengelola Informasi dan Dokumentasi (PPID) Tahun 2020 dilaksanakan di Hotel Wisata Baru yang dihadiri oleh Pejabat Pembantu PPID atau yang mewakili seluruh OPD di Kota Serang.</li><li>Acara dibuka oleh Kepala Dinas Komunikasi dan Informatika Kota Serang.</li></ul>', 1, 9, 'QR-MVSR8p-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (33, 1, '005/19-Org&RB/2020', 'Sekretariat Daerah Kota Serang', 'Undangan', 'Rabu', '2020-01-15', '2020-01-15', '08.00 s/d selesai', 272, 'Hotel Le Dian', 'Menghadiri Rapat Penilaian Perangkat Daerah di Lingkungan Pemerintah Kota Serang', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-15', '<ul><li>Rapat Penilaian Perangkat Daerah di Lingkungan Pemerintah Kota Serang dihadiri oleh seluruh Kasubag Umum dan Kepegawaian se-Kota Serang beserta Staf atau yang mewakili;</li><li>Narasumber Rapat Penilaian Perangkat Daerah di Lingkungan Pemerintah Kota Serang adalah dari Provinsi Banten dan Universitas Sultan Ageng Tirtayasa.<br></li></ul>', 1, 9, 'QR-JF1VX0-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (35, 1, '005/006.4-BPKAD/2020', 'BPKAD Kota Serang ', 'Undangan Rekonsiliasi BMD', 'Senin', '2020-01-13', '2020-01-13', '09.00 s/d 15.00', 272, 'Bidang Aset BPKAD Kota Serang', 'Menghadiri Rekonsiliasi BMD Triwulan IV Realisasi per 31 Desember 2019', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-13', '<ul><li>Telah dilaksanakan rekonsiliasi pelaporan Barang Milik Daerah (BMD) Realisasi Belanja Modal Tahun 2019 di Bidang Aset BPKAD Kota Serang.</li><li>Rekonsiliasi pelaporan BMD ini dilaksanakan untuk persiapan penyusunan Laporan Keuangan Tahun 2019.</li></ul>', 1, 9, 'QR-LYzSZK-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (36, 1, '005/162-Bapp/2020', 'WALIKOTA SERANG ', 'Rapat Koordinasi Pembahasan Rancangan LKPJ Walikota Serang TA 2019', 'Rabu', '2020-02-26', '2020-02-26', '09.00 WIB s.d Selesai', 272, 'Ledian Hotel, Kota Serang', 'Rapat Koordinasi Pembahasan Rancangan LKPJ Walikota Serang TA 2019.', 'Kendaraan Dinas Roda 4', 3, 1, '2020-02-25', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka Menghadiri Rapat Koordinasi Pembahasan Rancangan LKPJ Walikota Serang pada hari Rabu tanggal 26 Februari 2020 di Le Dian Hotel, Kota Serang.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Pada kegiatan ini dihadiri oleh Tim Penyusun LKPJ dan Kepala OPD beserta Kasubag PEP;</li><li>Kepala Daerah menyampaikan LKPJ kepada DPRD dalam rapat paripurna yang dilakukan 1 (satu) kali dalam 1 (satu) tahun paling lambat 3 (tiga) bulan setelah tahun anggaran berakhir;</li><li>Peserta rapat koordinasi pembahasan Rancangan LKPJ Walikota Serang Akhir Tahun Anggaran 2019 membawa bahan/ data capaian kinerja pembangunan, capaian kinerja program dan kegiatan 2019.&nbsp;<br></li></ol>', 1, 9, 'QR-3ZAuRH-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (37, 1, '800/06-BPBD/II/2020 ', 'BPBD', 'Undangan Forum Renja Perangkat Daerah', 'Senin', '2020-02-24', '2020-02-24', '09.00 WIB s/d Selesai ', 272, 'Hotel Abadi ', 'Undangan Forum Renja Perangkat Daerah', 'Kendaraan Dinas Roda 4', 5, 1, '2020-02-21', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri Forum Renja Perangkat Daerah Tahun 2021 pada hari Senin tanggal 24 Februari 2020 di Hotel Abadi:&nbsp;</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Rapat forum OPD diikuti dari beberapa OPD dilingkungan Pemerintah Kota Serang, Instansi Vertikal dan Organisasi Masyarakat yang terkait;</li><li>Program Unggulan dan Program Mendesak yang tercantum didalam RPJMD dijabarkan kedalam program dan kegiatan;</li><li>Tujuan Forum Perangkat Daerah berdasarkan Permendagri 86 Tahun 2017 pasal 136 yaitu bertujuan untuk memperoleh masukan dalam rangka penajaman target kinerja sasaran, program dan kegiatan, lokasi dan kelompok sasaran dalam Rancangan Renja Perangkat Daerah.<br></li></ol>', 1, 9, 'QR-agPKlf-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (38, 1, '800/57.2/Diskominfo/2020', 'Diskominfo', 'Undangan Rapat Penyusunan Pelaporan Capaian Kinerja Tahunan Perangkat Daerah', 'Jumat', '2020-02-21', '2020-02-21', '08.00 WIB s/d Selesai ', 272, 'Hotel Wisata Baru', 'Undangan Rapat Penyusunan Pelaporan Capaian Kinerja Tahunan Perangkat Daerah', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-20', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri rapat penyusunan capaian kinerja tahunan Perangkat Daerah pada hari Jumat tanggal 21 Februari 2020 di Hotel Wisata Baru.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Indikator kinerja harus memenuhi kriteria sebagai berikut: Spesifik (Specific), Dapat terukur (Measurable), Dapat dicapai (Attainable), Berjangka waktu tertentu (Time Bound), Dapat di pantau dan dikumpulkan (Trackable);</li><li>Evaluasi atas implementasi SAKIP yaitu: Evaluasi atas komponen SAKIP dan Penilaian dan penyimpulan;</li><li>Tujuan LKj-IP adalah memberikan informasi kinerja yang terukur kepada pemberi mandat atas kinerja yang telah seharusnya dicapai dan sebagai upaya perbaikan berkesinambungan bagi instansi pemerintah untuk meningkatkan kinerjanya;</li><li>Tujuan Permendagri 90 Tahun 2019: 1. Dari sisi perencanaan untuk memberikan pedoman bagi daerah aktivitas apa saja yang bisa dilakukan pemerintah daerah sesuai kewenangannya; 2. Dari sisi penganggaran, karena ada perubahan struktur belanja dalam PP 12/2019 maka dibutuhkan kodefikasi baru; 3. Dari sisi binwas, agar Pemerintah Pusat dan Pemprov mudah dalam melakukan pengendalian dan evaluasi.<br></li></ol><p><br></p>', 1, 9, 'QR-BpS40s-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (39, 1, '005/84-Diskominfo/2020', 'Diskominfo', 'Forum Renja Perangkat Daerah Tahun 2021', 'Kamis', '2020-02-20', '2020-02-20', '08.00 WIB s/d Selesai ', 272, 'Hotel Wisata Baru', 'Forum Renja Perangkat Daerah Tahun 2021', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-20', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri Forum Renja Perangkat Daerah Tahun 2021 pada hari Kamis tanggal 20 Februari 2020 di Hotel Wisata Baru.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Rapat Forum Renja OPD diikuti dari beberapa OPD dilingkungan Pemerintah Kota Serang, Instansi Vertikal dan Organisasi Masyarakat yang terkait;</li><li>Program Unggulan dan Program Mendesak yang tercantum didalam RPJMD dijabarkan kedalam program dan kegiatan;</li><li>Rancangan Prioritas Rencana Kerja Tahun 2021: Penguatan konektivitas jaringan, Peningkatan infrastruktur jaringan, Peningkatan kapasitas dan kapabilitas aparatur bidang TIK, Peningkatan pengelolaan pengaduan masyarakat (Rabeg dan Serang Siaga 112), Peningkatan pengelolaan keamanan persandian, Pengembangan dan integrasi aplikasi e-government, Peningkatan diseminasi informasi, Pengelolaan data satu pintu;</li><li>Tujuan Forum Perangkat Daerah Kota Serang untuk penyusunan Renja Perangkat Daerah Tahun 2021 yaitu salah satunya menyesuaikan pendanaan program dan kegiatan prioritas berdasarkan pagu indikatif untuk masing-masing Perangkat Daerah.<br></li></ol>', 1, 9, 'QR-1A4wDN-198208132009022002-.png', '198312102010011001', 121001);
INSERT INTO `sppd` VALUES (40, 1, '500/031.2-BPKAD/2020', 'BPKAD', 'Undangan Forum Renja Tahun 2021', 'Rabu', '2020-02-19', '2020-02-19', '08.00 WIB s/d Selesai ', 272, 'Hotel Wisata Baru', 'Undangan Forum Renja Tahun 2021', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-18', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri undangan Forum Renja BPKAD Kota Serang pada hari Rabu tanggal 19 Februari 2020 di Hotel Wisata Baru.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Rapat Forum Renja BPKAD Kota Serang diikuti dari beberapa OPD dilingkungan Pemerintah Kota Serang, Instansi Vertikal dan Organisasi Masyarakat yang terkait;</li><li>Tujuan Forum Perangkat Daerah berdasarkan Permendagri 86 Tahun 2017 Pasal 136 yaitu bertujuan untuk memperoleh masukan dalam rangka penajaman target kinerja sasaran, program dan kegiatan, lokasi dan kelompok sasaran dalam Rancangan Renja Perangkat Daerah.&nbsp;<br></li></ol>', 1, 9, 'QR-VAGgUQ-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (41, 1, '005/88-Bapp/2020', 'BAPPEDA', 'Undangan Ekspose Laporan Pendahuluan Penyusunan Dokumen Survei Kebutuhan Masyarakat (SKM) Kota Serang Tahun Anggaran 2020 ', 'Kamis', '2020-02-13', '2020-02-13', '13.00 WIB - Selesai', 272, 'Ruang Rapat Bappeda Kota Serang', 'Undangan Ekspose Laporan Pendahuluan Penyusunan Dokumen Survei Kebutuhan Masyarakat (SKM) Kota Serang Tahun Anggaran 2020 ', 'Kendaraan Dinas Roda 4', 3, 1, '2020-02-12', '', 1, 9, 'QR-tqT7KY-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (42, 1, '005/114-Pemt/II/2020', 'Sekretariat Daerah', 'Undangan Peserta Sosialisasi dan Validasi Data Penyusunan LPPD Tahun 2019, Tahun Anggaran 2020', 'Rabu', '2020-02-12', '2020-02-12', '13.00 WIB - Selesai', 272, 'Hotel Wisata Baru', 'Undangan Peserta Sosialisasi dan Validasi Data Penyusunan LPPD Tahun 2019, Tahun Anggaran 2020', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-11', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalan rangka&nbsp;<span style=\'display: inline !important; float: none; background-color: transparent; color: rgb(33, 37, 41); font-family: \"Source Sans Pro\",-apple-system,BlinkMacSystemFont,\"Segoe UI\",Roboto,\"Helvetica Neue\",Arial,sans-serif,\"Apple Color Emoji\",\"Segoe UI Emoji\",\"Segoe UI Symbol\"; font-size: 16px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\'>Sosialisasi dan Validasi Data Penyusunan LPPD Tahun 2019, Tahun Anggaran 2020 pada hari Rabu tanggal 12 Februari 2020 di Hotel Wisata Baru.</span></p><p><span style=\'display: inline !important; float: none; background-color: transparent; color: rgb(33, 37, 41); font-family: \"Source Sans Pro\",-apple-system,BlinkMacSystemFont,\"Segoe UI\",Roboto,\"Helvetica Neue\",Arial,sans-serif,\"Apple Color Emoji\",\"Segoe UI Emoji\",\"Segoe UI Symbol\"; font-size: 16px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\'>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</span></p><ol><li>Laporan Penyelenggaraan Pemerintah Daerah (LPPD) juga menggambarkan pencapaian kinerja dalam Penyelenggaraan urusan-urusan Pemerintah yang dimaksud adalah urusan wajib dan urusan pilihan dan fungsi penunjang urusan pemerintah daerah;</li><li>Adanya perbedaan capaian kinerja setelah dilakukan evaluasi yang disebabkan berbedanya elemen data dengan dokumen pendukung (terkecuali Laporan Keuangan);</li><li>Syarat penyajian data pada IKK: 1. Validitas: Data yang disajikan harus disahkan oleh OPD yang bertanggung jawab atas kinerja; 2. Akurat: Angka, Rasio, Ada/Tepat sesuai dengan data yang disahkan oleh pejabat OPD yang bertanggung jawab atas kinerja; 3. Rasional: Data yang disajikan harus masuk akal.<br></li></ol>', 1, 9, 'QR-LFiOxR-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (43, 1, '005/116-Bapp/2020', 'Sekretariat Daerah', 'Persiapan Penyusunan RKPD dan Renja PD Tahun 2021', 'Rabu', '2020-02-12', '2020-02-12', '08.00 WIB - Selesai ', 272, 'Rumah Makan Cibiuk Kelapa Dua - Kota Serang ', 'Persiapan Penyusunan RKPD dan Renja PD Tahun 2021', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-10', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka Sosialisasi SE Walikota Serang tentang Penyusunan RKPD dan Renja PD Tahun 2021 pada hari Rabu tanggal 12 Februari 2020 di Rumah Makan Cibiuk Kelapa Dua - Kota Serang.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Rencana Implementasi Peraturan Pemerintah Nomor 12 Tahun 2019 tentang Pengelolaan Keuangan Daerah dan Peraturan Menteri Dalam Negeri Nomor 90 Tahun 2019 tentang Klasifikasi, Kodefikasi, dan Nomenklatur Perencanaan Pembangunan dan Keuangan Daerah pada Perencanaan Kota Serang Tahun 2021;</li><li>Klasifikasi, Kodefikasi, dan Nomenklatur Perencanaan Pembangunan dan Keuangan Daerah yang selanjutnya disebut Klalsifikas, Kodefikasi dan Nomenklatur adalah penggolongan, pemberian kode dan daftar penamaan perencanaan pembangunan dan keuangan daerah yang disusun secara sistematis sebagai acuan dalam penyusunan dokumen perencanaan pembangunan daerah dan keuangan daerah.&nbsp;</li></ol>', 1, 9, 'QR-fNhiMW-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (44, 1, '', '', 'Menyampaikan Hasil Lampiran IKK 3 ', 'Jumat', '2020-01-31', '2020-01-31', '08.00 WIB - Selesai ', 272, 'Bagian Pemerintahan Setda Kota Serang ', 'Menyampaikan Hasil Lampiran IKK 3 ', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-30', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka Menyampaikan IKK 3 pada hari Jumat tanggal 31 Januari 2020 di Bagian Pemerintahan Setda Kota Serang.</p><p>Hasil dari Perjalanan Dalam Daerah adalah sebagai berikut:</p><ol><li>Setelah mengirim IKK 1 &amp; 2 ada kekurangan format dari Bagian Pemerintahan yang harus di kerjakan kembali yaitu IKK 3 dan sudah diselesaikan dan di sampaikan ke Bagian Pemerintahan Setda Kota Serang.</li></ol>', 1, 9, 'QR-khEeGF-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (45, 1, '005/51-Bapp/2020', 'WALIKOTA SERANG ', 'Undangan Forum Konsultasi Publik Rancangan Awal RKPD Kota Serang Tahun 2021', 'Rabu', '2020-01-29', '2020-01-29', '08.30 WIB s/d Selesai ', 272, 'Le Dian Hotel', 'Undangan Forum Konsultasi Publik Rancangan Awal RKPD Kota Serang Tahun 2021                                ', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-29', '<p>Dipermaklumkan Dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri undangan rapat Forum Konsultasi Publik Rancangan Awal RKPD Kota Serang Tahun 2021 pada hari Rabu tanggal 29 Januari 2020 di Le Dian Hotel Serang.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Penyusunan Rancangan Awal Rencana Kerja Pemerintah Daerah (RKPD) Kota Serang 2021 sesuai dengan amanat pada pasal 80 ayat (1) Peraturan Menteri Dalam Negeri Nomor 86 Tahun 2017 Tentang Tata Cara Perencanaan, Pengendalian dan Evaluasi Pembangunan Daerah, Tata Cara Evaluasi Rancangan Peraturan Daerah Tentang RPJPD dan RPJMD, serta Tata Cara Perubahan RPJPD, RPJMD dan RKPD;</li><li>Kegiatan ini bertujuan untuk mendapatkan masukan dan saran guna penyempurnaan rancangan awal RKPD, menjadi pedoman dalam musrenbang RKPD di tingkat kecamatan dan sebagai pedoman penyempurnaan rancangan awal rencana perangkat daerah;</li><li>Dalam kegiatan ini dihadiri semua perwakilan masyarakat dan OPD, agar komprehensif semua masukannya guna mengetahui tahun 2021 apa yang diprioritas pembangunannya.</li></ol>', 1, 9, 'QR-zFBPEn-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (46, 1, '', 'Bappeda', 'Koordinasi tentang capaian indikator kinerja', 'Selasa ', '2020-01-28', '2020-01-28', '08.00 WIB - Selesai ', 272, 'Bappeda Kota Serang ', 'Koordinasi tentang capaian indikator kinerja', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-27', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka Koordinasi tentang capaian indikator kinerja pada hari Selasa tanggal 28 Januari 2020 di Bappeda Kota Serang.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Masih kurangnya data indikator kinerja pembangunan daerah mengacu pada Pemendagri 86 Tahun 2017.</li></ol>', 1, 9, 'QR-W8Tkfx-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (47, 1, '060/52-Org&RB/2020', 'Sekretariat Daerah', 'Bimtek Penyusunan IKU, Perjanjian Kinerja dan Pelaporan Kinerja TA 2020', 'Senin', '2020-01-27', '2020-01-27', '08.00 WIB - Selesai ', 272, 'Rumah Makan \"Sari Kuring Indah\"', 'Bimtek Penyusunan IKU, Perjanjian Kinerja dan Pelaporan Kinerja TA 2020', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-24', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka menghadiri undangan rapat&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Bimtek Penyusunan IKU, Perjanjian Kinerja dan Pelaporan Kinerja TA 2020 pada hari Senin tanggal 27 Januari 2020 di Sari Kuring Indah Ciracas Serang.</span></p><p><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</span></p><ol><li><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Kegiatan ini dalam rangka Bimbingan Teknis Penyusunan Indikator Kinerja Utama, Perjanjian Kinerja dan Laporan Kinerja Perangkat Daerah;</span></li><li><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Pada kegiatan ini dihadiri oleh Kasubag PEP dan Operator pada Setiap OPD di Pemerintahan Kota Serang;</span></li><li><font color=\"#212529\">Perjanjian Kinerja yaitu suatu dokumen pernyataan kinerja /kesepakatan kerja/ perjanjian kinerja antara atasan dan bawahan untuk mewujudkan target kinerja tertentu berdasarkan pada sumber daya yang dimiliki oleh instansi;</font></li><li><font color=\"#212529\">Laporan Kinerja yaitu laporan kinerja tahunan yang berisi pertanggung jawaban kinerja suatu instansi dalam mencapai tujuan / sasaran strategis instansi.&nbsp;</font></li></ol>', 1, 9, 'QR-a9ye3E-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (48, 1, '050/047-Bapp/2020', 'Sekretariat Daerah', 'Undangan Rapat Koordinasi Persiapan Penyusunan LKPJ ', 'Rabu ', '2020-01-22', '2020-01-22', '08.30 WIB s/d Selesai ', 272, 'RM. Cibiuk, Kelapa Dua, Kagungan Kota Serang ', 'Undangan Rapat Koordinasi Persiapan Penyusunan LKPJ ', 'Kendaraan Dinas Roda 4', 4, 1, '2020-01-21', '<p>Dipermaklumkan dengan Hormat, dengan ini dilaporkan Perjalanan Dinas Dalam Daerah dalam rangka Menghadiri Undangan Rapat Koordinasi Persiapan Penyusunan LKPJ pada hari Rabu tanggal 22 Januari 2020 di RM. Cibiuk.</p><p>Hasil dari Perjalanan Dinas Dalam Daerah adalah sebagai berikut:</p><ol><li>Membahas tentang penyusunan Laporang Keterangan Pertanggung Jawaban (LKPJ);</li><li>Peserta rapat koordinasi persiapan penyusunan LKPJ membawa Laporan Evaluasi Renja Tahun 2019 yang telah sesuai dengan format yang sudah disampaikan;</li><li>LKPJ disusun berdasarkan pelaksanaan program dan kegiatan yang ditetapkan dalam dokumen perencanaan dan anggaran tahunan.</li></ol>', 1, 9, 'QR-AMxpkS-198208132009022002-.png', '198208132009022002', 121001);
