INSERT INTO `sppd` VALUES (96, 1, '', '', '', 'Selasa', '2020-02-04', '2020-02-04', '08.00 s/d selesai', 272, 'KPP Pratama Serang Barat', 'Melakukan Koordinasi Narasumber Pajak Kegiatan Penyusunan Laporan Akhir 2019', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-04', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah dalam kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Koordinasi Narasumber Pajak Kegiatan Penyusunan Laporan Akhir 2019</span><span style=\"color: rgb(33, 37, 41);\">&nbsp;</span>pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar Daerah ke&nbsp;<span style=\"color: rgb(33, 37, 41);\">KPP Pratama Serang Barat</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;pada hari Selasa tanggal 04 Februari 2020</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan <font color=\"#212529\">Koordinasi Narasumber Pajak Kegiatan Penyusunan Laporan Akhir Tahun 2019 adalah meminta atau mengundang Narasumber dari Kantor Pajak Pratama Kota Serang Khususnya pengawasan konsultasi (waskon 3) untuk memberi materi tentang perpajakan Pph 21,22,23 dan 24 serta aturan PMK 141 tahun 2015 untuk kegiatan Penyusunan Laporan Keuangan Akhir Tahun Diskominfo Tahun 2019.</font></p>', 1, 9, 'QR-Lr6Axn-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (97, 1, '', '', '', 'Kamis', '2020-02-06', '2020-02-06', '08.00 s/d selesai', 272, 'Gedung Serba Guna Wanda Galuh', 'Mengikuti Laporan Rekonsiliasi Realisasi Anggaran Belanja Pemerintah Kota Serang Triwulan IV TA 2019', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-06', '<p>&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah dalam kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Rekonsiliasi Realisasi Anggaran Belanja Pemerintah Kota Serang Triwulan IV TA 2019</span><span style=\"color: rgb(33, 37, 41);\">&nbsp;</span>pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar Daerah ke&nbsp;<span style=\"color: rgb(33, 37, 41);\">Gedung Serba Guna Wanda GaluhJl. Raya Serang-Pandeglang, Palima, Serang-Banten</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;pada hari Kamis Tanggal 06 Februari 2020</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">Rekonsiliasi Realisasi Anggaran Belanja Pemerintah Kota Serang Triwulan IV TA 2019 adlah bahwa Dinas Komunikasi dan Informatika Kota Serang telah balance dan bisa lanjut dalam penyusunan Calk 2019</span><span style=\"font-size: 1rem;\">&nbsp;</span><font color=\"#212529\" style=\"font-size: 1rem;\">.</font></p>', 1, 9, 'QR-DCGZg4-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (98, 1, '005/023-BPKAD/2020', 'BPKAD Kota Serang', 'Undangan', 'Jumat', '2020-02-07', '2020-02-07', '08.00 s/d selesai', 272, 'Hotel Le Dian & Cottages, Serang', 'Pengarahan Penyusunan CaLK TA 2019', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-07', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah dalam kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Penyusunan Calk Tahun 2019&nbsp;</span>pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar Daerah ke&nbsp;<span style=\"color: rgb(33, 37, 41);\">Le Dian Hotel &amp;Cottages, Serang, </span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">pada hari Jumat Tanggal 07 Februari 2020</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">.</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">&nbsp;Penyusunan Calk Tahun 2019 adalah kegiatan tersebut dihadiri oleh masing-masing perwakilan OPD se-Kota Serang. Kegiatan tersebut di buka oleh Bapak H. Asep Saprudin, SE, MM, Ak selaku Kepala Akuntansi BPKAD Kota Serang dan sekaligus memberikan materi tentang Sistematika Catatan Atas Laporan Keuangan SKPD dengan penambahan pada BAB V yaitu yaitu Informasi Penting lainnya yang berisi Komitmen/Kontinjensi, Kejadian-kejadian penting setelah tanggal neraca, Rekening SKPD, Penerimaan Kas yang tidak diterima melalui Rekening Kas Umum Daerah, Temuan dan Tindaklanjut Temuan BPK-RI, Kegiatan teng melampaui akhir tahun, Pengungkapan Inventarisasi Aset Tetap dan Perubahan Pejabat Pengelolaan Keuangan/Perbendaharaan.</span></p><p>&nbsp; &nbsp; Demikian disampaikan, atas perhatiannya diucapkan terima kasih.&nbsp;<span style=\"color: rgb(33, 37, 41);\"><br></span></p>', 1, 9, 'QR-Ngeboz-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (99, 1, '900/025.3/BPKAD/2020', 'BPKAD', 'Pemutahiran User dan Bimtek IBC Tahun 2020', 'Senin', '2020-02-10', '2020-02-10', '08.00 s/d selesai', 272, 'Aula Bank BJB KCK Banten ', 'Pemutahiran User dan Bimtek IBC Tahun 2020', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-10', '<p>&nbsp; &nbsp; Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah Kegiatan Pemutakhiran User dan Bimtek IBC Tahun 2020 pada kegiatan Rapat-rapat koordinasi dan Konsultasi Dalam dan Luar Daerah ke Aula Bank BJB KCK Banten pada Hari Senin Tanggal 10 Februari 2020.&nbsp;</p><p>&nbsp; &nbsp; Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan Pemutakhiran User dan Bimtek IBC Tahun 2020 kegiatan tersebut dihadiri oleh masing-masing perwakilan OPD se-Kota Serang. Maksud dari kegiatan ini adalah Launching penggunaan Internet Banking Corporate (IBC) di semua OPD yang ada di Lingkungan Pemerintah Kota Serang.&nbsp;</p><p>&nbsp; &nbsp; Demikian disampaikan, atas perhatiannya diucapkan terima kasih.&nbsp;<br></p>', 1, 9, 'QR-iwdKvI-198208132009022002-.png', '198208132009022002', 121001);
