INSERT INTO `sppd` VALUES (103, 1, '005/041-Diskominfo/2020', 'Diskominfo ', 'Undangan Dalam Rangka mensinergitaskan program-program Pemerintah Provinsi Banten Tahun 2020 dan Perencanan Kegiatan Tahun 2021 dengan Kabupaten/Kota.', 'Kamis', '2020-01-30', '2020-01-30', '09.00 wib s/d Selesai ', 272, 'Aula Lt. 2 Dinas Perhubungan Provinsi Banten KP3B Curug Kota Serang ', 'Undangan Dalam Rangka mensinergitaskan program-program Pemerintah Provinsi Banten Tahun 2020 dan Perencanan Kegiatan Tahun 2021 dengan Kabupaten/Kota.', 'Kendaraan Dinas Roda 4', 4, 1, '2020-02-03', '<p>&nbsp; &nbsp; Dipermaklumkan dengan hormat, dengan ini dilaporkan hasil Rapat Koordinasi Pengelolaan Komunikasi Kabupaten/Kota, Pada Hari Kamis, 30 Januari 2020 di Aula Lt. 2 Dinas Perhubungan Provinsi Banten, sbb:</p><p>1. Dasar melakukan perjalanan dinas</p><p>&nbsp; &nbsp; <i>1. Surat Perintah Kepala Dinas Kominfo Kota Serang&nbsp;</i></p><p>&nbsp; &nbsp; <i>2. Surat Undangan Nomor: 041-Diskominfo/2020</i></p><p>&nbsp; &nbsp; Kesimpulan Hasil Perjalanan Dinas&nbsp;</p><p>&nbsp; &nbsp; - Telah selesai Rapat Koordinasi Dalam ranga Mensinergitaskan Program Tahun 2021 dengan&nbsp; &nbsp; &nbsp; &nbsp; Kabupaten/Kota.</p><p>2. Penutup&nbsp;</p><p>&nbsp; &nbsp; Demikian laporan ini dibuat sebagai bentuk pertanggung jawaban dalam Rangka &nbsp;&nbsp;&nbsp;&nbsp;melaksanakan kegiatan seperti tersebut diatas.&nbsp;<br><br></p>', 1, 9, 'QR-lzVXsc-198208132009022002-.png', '198312102010011001', 121001);
