INSERT INTO `sppd` VALUES (202, 1, '', '', '', 'Senin', '2020-06-15', '2020-06-15', '08.00 s/d selesai', 272, 'BKPSDM Kota Serang', 'Menyampaikan Laporan Pendataan PNS yang terkena Covid-19 dan Pendataan PNS yang dijatuhi Hukuman Disiplin karena berpergian keluar daerah dan atau Mudik', 'Kendaraan Dinas Roda 4', 4, 1, '2020-06-15', '<p style=\"text-align: justify; \">&nbsp;&nbsp;&nbsp;&nbsp;Dipermaklumkan dengan Hormat, dengan ini dilaporkan Hasil Perjalanan Dinas Dalam Daerah dalam kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Menyampaikan Laporan Pendataan PNS yang terkena Covid-19 dan Pendataan PNS yang dijatuhi Hukuman Disiplin karena berpergian keluar daerah dan atau Mudik</span>&nbsp;ke BKPSDM Kota Serang<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;pada hari Senin tanggal 15 Juni 2020</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">.</span></p><p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;Hasil dari Perjalanan Dinas Dalam Daerah Kegiatan&nbsp;<span style=\"color: rgb(33, 37, 41);\">Tersebut</span><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">&nbsp;adalah PNS Diskominfo Kota Serang tidak ada yang terkena Covid-19 dan tidak ada yang dijatuhi Hukuman Disiplin karena berpergian keluar daerah dan atau Mudik.</span></p>', 1, 9, 'QR-9jdDLn-198208132009022002-.png', '198208132009022002', 121001);
