INSERT INTO `sppd` VALUES (250, 1, '005/072.1-BPKAD/2020', 'BPKAD ', 'Verifikasi DPA Hasil Rasionalisasi TA. 2020', 'Kamis, Jumat & Senin', '2020-04-16', '2020-04-17', '08.00 Wib s/d Selesai', 272, 'Ruang Kerja Masing-masing Verifikator ', 'Verifikasi DPA Hasil Rasionalisasi TA. 2020                                ', 'Kendaraan Dinas Roda 2', 4, 1, '2020-04-16', '<p>Pada Pergeseran Tahap 2 hasil Rasionalisasi TA. 2020, kegiatan yang terkena Refocusing Anggaran yaitu Kegiatan Penyusunan Pelaporan Keuangan Triwulanan dan Semesteran Rp 5.612.000, Kegiatan Penyusunan Pelaporan Keuangan Akhir Tahun Rp 1.250.000, Kegitan Penyusunan Dokumen Perencanaan Perangkat Daerah Rp 17.850.000, Kegiatan Penyediaan Infrastruktur dan Layanan Telekomunikasi Rp 200.950.000, Kegiatan Penyebarluasan Informasi Pembangunan Daerah Rp 6.695.200, Kegiatan Penyebarluasan Informasi Penyelenggaraan Pemerintah Daerah Rp 59.000.000, Kegiatan Kemitraan dan Pembinaan Lembaga Media Pers dan Penyiaran Rp 26.254.000, Kegiatan Pembinaan Kelompok Informasi Masyarakat Rp 39.290.800.<br></p>', 1, 9, 'QR-SGyIzD-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (251, 1, '', '', '', 'Kamis', '2020-03-19', '2020-03-19', '09.00 WIB s/d Selesai', 272, 'Ruang Kerja Masing-masing Verifikator', 'Verifikasi & Penandatanganan DPA Pergeseran Tahap 1', 'Kendaraan Dinas Roda 2', 4, 1, '2020-03-18', '<p>Pada Pergeseran Tahap 1 ada beberapa Kegiatan yang Bergeser, yaitu Kegiatan Administrasi Perkantoran, Pengadaan Sarana dan Prasarana Kantor, Penyediaan Makanan dan Minuman, Pemeliharaan Infrastruktur Teknologi Informasi dan Komunikasi, Pembinaan, Pengawasan dan Pengendalian Teknologi Informasi dan Komunikasi, Pengelolaan Informasi dan Dokumentasi Pemerintah Daerah, Pengelolaan dan Perlindungan Teknologi Informasi, Penyusunan dan Pengumpulan Data dan Statistik Daerah, Pengolahan, Updating dan Analisis Data dan Statistik Daerah.<br></p>', 1, 9, 'QR-2XnKAq-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (252, 1, '', '', '', 'Senin', '2020-05-11', '2020-05-11', '09.00 WIB s/d Selesai', 272, 'Ruang Kerja Masing-masing Verifikator', 'Verifikasi & Penandatanganan DPA Pergeseran Tahap 3', 'Kendaraan Dinas Roda 2', 4, 1, '2020-05-11', '<p>Pada Pergeseran Tahap 3 ini mengalami Pengurangan pada Gaji Pokok dan di Pindahkan/Penambahan pada Komponen Tunjangan PPH/Tunjangan Khusus sebesar Rp 5.251.957 &amp; Iuran Jaminan Kesehatan sebesar Rp 104.320.329. <br></p>', 1, 9, 'QR-Q89qYw-198208132009022002-.png', '198208132009022002', 121001);
INSERT INTO `sppd` VALUES (253, 1, '', '', '', 'Kamis', '2020-06-04', '2020-06-04', '09.00 WIB s/d Selesai', 272, 'Ruang Kerja Masing-masing Verifikator', 'Verifikasi & Penandatanganan DPA Pergeseran Tahap 4', 'Kendaraan Dinas Roda 2', 4, 1, '2020-03-18', '<p>Pada Pergeseran Tahap 4 ini Kegiatan yang bergeser yaitu Pelayanan Administrasi Perkantoran, Penyediaan Makanan dan Minuman, Penyediaan Infrastruktur dan Layanan Telekomunkasi.<br></p>', 1, 9, 'QR-P5hZqd-198208132009022002-.png', '198208132009022002', 121001);
